import { createSlice, PayloadAction } from "@reduxjs/toolkit";


interface CartProduct {
    id?: string;
    category?: string;
    name?: string;
    price?: number;
    image?: string;
    quantity?: number;
    color?: string;
}

interface CartProducts {
    items: Array<CartProduct>;
}

const initialState: CartProducts | any = {
    cart: []
}

const FilerProduct = createSlice({
    name: 'filter',
    initialState,
    reducers: {

        filterProduct(state, { payload }: PayloadAction<CartProduct>) {
            // todo filter po coast
            state.cart.forEach((item: any) => {
                if (item.id === payload.id && item.color === payload.color) {
                    // console.log('ODINAKOVI ', payload.id);
                    item.quantity += payload.quantity;
                    return
                }
            })
        },


    },
    extraReducers: (builder) => {

    },

})

export const FilerProductReduser = FilerProduct.reducer;
export const { filterProduct } = FilerProduct.actions
