import { createSlice, isAnyOf, PayloadAction } from "@reduxjs/toolkit";
import { setCartProduct } from "../../thunks/setCartProduct";


interface CartProduct {
    id: string; 
    category: string;
    name: string;
    price: number;
    image: string;
    quantity: number;
}

interface CartProducts {
    items: CartProduct[];
}

const initialState: CartProducts | {} = {
    cart: []
}

const CartProduct = createSlice({
    name: 'cartProduct',
    initialState,
    reducers: { 
        // обычный редюсер !! ?
    },
    extraReducers: (builder) => {
        builder
            .addMatcher(
                isAnyOf(setCartProduct.fulfilled),
                (state, action: PayloadAction<any>) => {
                    console.log(state, 'state!!--cart');
                    console.log(action.payload, 'action.payload');
                    // const newState = [...state.cart]
                    state.cart = action.payload;
                }
            );
    },

})

export const CartProductReduser = CartProduct.reducer;
export const {} = CartProduct.actions
