import { useFormik } from "formik";
import React, { FC, useState } from "react";
import { AddProductWrapper, BlockInputs, ButtonSubmit, InputAddProduct, TitleAdd } from "./AddProduct.styled";

const initialState = {
    name: "",
    categoryA: "",
    tagsA: "",
    length: 0,
    weight: 0,
    country: "",
    price: 0,
}

const AddProduct: FC = () => {
    const [product, setProduct] = useState<Object>(initialState);
    // const formData = new FormData();
    const form = useFormik({
        initialValues: {
            title: '',
            color: ''
        },
        validateOnChange: false,
        onSubmit(values, formikHelpers) {

        }
    });

    return (
        <AddProductWrapper>
            <div>
                <InputAddProduct
                    // name="name"
                    // label={"title"}
                    value={form.values.title}
                    onChange={(e) => {
                        form.setFieldValue("title", e.target.value);
                    }}
                    onBlur={() => { form.setFieldTouched('title', true) }}
                />
                                <InputAddProduct
                    // name="name"
                    // label={"color"}
                    value={form.values.title}
                    onChange={(e) => {
                        form.setFieldValue("color", e.target.value);
                    }}
                    onBlur={() => { form.setFieldTouched('color', true) }}
                />
            </div>
            <TitleAdd>
                добавление товара
            </TitleAdd>
            <BlockInputs>
                <InputAddProduct placeholder="name" />
                name
            </BlockInputs>
            <BlockInputs>
                <InputAddProduct placeholder="categoryA" />
                categoryA
            </BlockInputs>
            <BlockInputs>
                <InputAddProduct placeholder="tagsA" />
                tagsA
            </BlockInputs>
            <BlockInputs>
                <InputAddProduct placeholder="length" />
                length
            </BlockInputs>
            <BlockInputs>
                <InputAddProduct placeholder="weight" />
                weight
            </BlockInputs>
            <BlockInputs>
                <InputAddProduct placeholder="country" />
                country
            </BlockInputs>
            <BlockInputs>
                <InputAddProduct placeholder="price" />
                price
            </BlockInputs>
            <BlockInputs>
                <InputAddProduct placeholder="picture" type="file" />
                picture
            </BlockInputs>
            <ButtonSubmit onClick={() => { }}>добавить товар</ButtonSubmit>
        </AddProductWrapper>
    )
}

export default AddProduct;
