import React, { FC, useState } from "react"
import { useAppDispatch } from "../../redux";
import { filter2Products, filterProducts } from "../../redux/reducers/productsReducer/productsReducer";
// import { filterProducts } from "../../redux/reducers/productsReducer/productsReducer";
import FilterItem from "../FilterItem/FilterItem";
import { FilterBlockWrapper, FilterCoast, FilterCompound, HeaderBlock, Input, InputCheckbox } from "./FilterBlock.styled";

const FilterBlock: FC<any> = ({ localData, setFilter }) => {
    const dispatch = useAppDispatch();
    const [min, setMin] = useState(0);
    const [max, setMax] = useState(900000);
    let number = { min: 400, max: 800 };

    const minChangeValue = (e: any) => {
        setMin(prev => prev = e.target.value)
    }

    const maxChangeValue = (e: any) => {
        setMax(prev => prev = e.target.value)
    }


    return (
        <FilterBlockWrapper>
            <button onClick={() => { setFilter({ min: min, max: max }) }}>filter!</button>
            <HeaderBlock>
                фильтр по пряже:
            </HeaderBlock>
            <FilterCoast>
                <HeaderBlock>
                    Стоимость:
                </HeaderBlock>
                <Input placeholder="от" onChange={minChangeValue} />
                <Input placeholder="до" onChange={maxChangeValue} />
            </FilterCoast>
            <FilterItem localState={localData[0]} header={localData[2][0]} />
            <FilterItem localState={localData[1]} header={localData[2][1]} />
            {/* <FilterItem localState={localData[2]} header={localData[3][2]} /> */}

        </FilterBlockWrapper>
    )
}

export default FilterBlock;


